import axios, { AxiosInstance, AxiosError, AxiosHeaders } from 'axios';

// API Response Types
export interface ApiResponse<T> {
  data?: T;
  error?: string;
  success: boolean;
}

export interface AuthResponse {
  access_token: string;
  token_type: string;
  expires_in: number;
}

export interface SignupData {
  email: string;
  username: string;
  password: string;
}

export interface LoginData {
  username: string;
  password: string;
}

export interface AgentResponse {
  id: string;
  type: 'trading' | 'defi';
  status: 'running' | 'stopped' | 'error';
  lastUpdated: string;
}

export interface StrategyResponse {
  id: string;
  name: string;
  type: string;
  parameters: Record<string, any>;
  status: 'active' | 'inactive';
  createdAt: string;
}

export interface WalletResponse {
  address: string;
  publicKey: string;
  balance: string;
  transactions: Array<{
    hash: string;
    type: string;
    amount: string;
    status: string;
    timestamp: string;
  }>;
}

class ApiClient {
  private client: AxiosInstance;

  constructor() {
    this.client = axios.create({
      baseURL: `${process.env.REACT_APP_API_URL || 'http://localhost:8000'}/api/v1`,
      headers: {
        'Content-Type': 'application/json',
      },
    });

    // Add request interceptor to include JWT token and handle token refresh
    this.client.interceptors.request.use(async (config) => {
      const token = localStorage.getItem('token');
      const tokenExpiry = localStorage.getItem('token_expiry');

      if (token && tokenExpiry) {
        // Check if token is about to expire (within 5 minutes)
        const expiryTime = parseInt(tokenExpiry);
        const currentTime = Date.now();
        const timeToExpiry = expiryTime - currentTime;

        console.log('Token status in request interceptor:', {
          currentTime: new Date(currentTime).toISOString(),
          expiryTime: new Date(expiryTime).toISOString(),
          timeToExpiry: Math.floor(timeToExpiry / 1000) + ' seconds'
        });

        if (currentTime + 5 * 60 * 1000 >= expiryTime && !config.url?.includes('/auth/refresh')) {
          console.log('Token refresh needed in request interceptor');
          try {
            const response = await this.refreshToken();
            if (response.success && response.data?.access_token) {
              // Update tokens in localStorage
              localStorage.setItem('token', response.data.access_token);
              localStorage.setItem('token_expiry', String(Date.now() + (response.data.expires_in * 1000)));

              // Verify token storage
              const newToken = localStorage.getItem('token');
              const newExpiry = localStorage.getItem('token_expiry');

              if (!newToken || !newExpiry) {
                console.error('Failed to store refreshed tokens in request interceptor');
                localStorage.removeItem('token');
                localStorage.removeItem('token_expiry');
                window.location.href = '/login';
                return Promise.reject(new Error('Failed to store authentication tokens'));
              }

              config.headers.Authorization = `Bearer ${newToken}`;
              return config;
            }
          } catch (error) {
            console.error('Token refresh failed in request interceptor:', error);
            // Clear tokens and redirect to login if refresh fails
            localStorage.removeItem('token');
            localStorage.removeItem('token_expiry');
            window.location.href = '/login';
            return Promise.reject(error);
          }
        }

        config.headers.Authorization = `Bearer ${token}`;
      }
      return config;
    });

    // Add response interceptor for error handling with enhanced logging and token verification
    this.client.interceptors.response.use(
      (response) => response,
      async (error: AxiosError) => {
        const originalRequest = error.config;
        console.log('Response error intercepted:', {
          status: error.response?.status,
          url: originalRequest?.url,
          method: originalRequest?.method
        });

        if (error.response?.status === 401 && originalRequest && !originalRequest.url?.includes('/auth/refresh')) {
          console.log('Unauthorized error detected, attempting token refresh');
          try {
            const response = await this.refreshToken();
            if (response.success && response.data?.access_token) {
              // Update tokens in localStorage
              localStorage.setItem('token', response.data.access_token);
              localStorage.setItem('token_expiry', String(Date.now() + (response.data.expires_in * 1000)));

              // Verify token storage
              const newToken = localStorage.getItem('token');
              const newExpiry = localStorage.getItem('token_expiry');

              if (!newToken || !newExpiry) {
                console.error('Failed to store refreshed tokens in response interceptor');
                localStorage.removeItem('token');
                localStorage.removeItem('token_expiry');
                window.location.href = '/login';
                return Promise.reject(new Error('Failed to store authentication tokens'));
              }

              console.log('Token refresh successful, retrying original request');
              // Retry the original request with new token
              const newRequest = { ...originalRequest };
              if (newRequest.headers instanceof Object) {
                newRequest.headers = new AxiosHeaders({
                  ...originalRequest.headers,
                  Authorization: `Bearer ${newToken}`
                });
              }
              return this.client.request(newRequest);
            }
          } catch (refreshError) {
            console.error('Token refresh failed in response interceptor:', refreshError);
            // Clear tokens before redirecting
            localStorage.removeItem('token');
            localStorage.removeItem('token_expiry');
          }

          console.log('Authentication failed, redirecting to login');
          // If refresh failed, redirect to login
          window.location.href = '/login';
        }
        return Promise.reject(error);
      }
    );
  }

  // Auth Management
  async signup(data: SignupData): Promise<ApiResponse<AuthResponse>> {
    try {
      console.log('Attempting signup:', { email: data.email, username: data.username });
      const formData = new URLSearchParams();
      formData.append('email', data.email);
      formData.append('username', data.username);
      formData.append('password', data.password);

      const response = await this.client.post('/auth/signup', formData.toString(), {
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
      });

      if (response.data.access_token) {
        // Set tokens in localStorage
        localStorage.setItem('token', response.data.access_token);
        localStorage.setItem('token_expiry', String(Date.now() + (response.data.expires_in * 1000)));

        // Verify token storage
        const token = localStorage.getItem('token');
        const tokenExpiry = localStorage.getItem('token_expiry');

        if (!token || !tokenExpiry) {
          console.error('Failed to store tokens after signup');
          localStorage.removeItem('token');
          localStorage.removeItem('token_expiry');
          throw new Error('Failed to store authentication tokens');
        }

        console.log('Signup successful, tokens stored');
        return { data: response.data, success: true };
      }

      console.error('Signup response missing access token');
      return { error: 'Invalid server response', success: false };
    } catch (error) {
      const errorMessage = this.handleError(error);
      console.error('Signup error:', errorMessage);
      // Ensure no partial auth state remains
      localStorage.removeItem('token');
      localStorage.removeItem('token_expiry');
      return { error: errorMessage, success: false };
    }
  }

  async login(data: LoginData): Promise<ApiResponse<AuthResponse>> {
    try {
      console.log('Attempting login:', { username: data.username });
      const formData = new URLSearchParams();
      formData.append('username', data.username);
      formData.append('password', data.password);
      formData.append('grant_type', 'password');

      const response = await this.client.post('/auth/login', formData.toString(), {
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        withCredentials: true, // Required for cookies
      });

      if (response.data.access_token) {
        // Set tokens in localStorage
        localStorage.setItem('token', response.data.access_token);
        localStorage.setItem('token_expiry', String(Date.now() + (response.data.expires_in * 1000)));

        // Verify token storage
        const token = localStorage.getItem('token');
        const tokenExpiry = localStorage.getItem('token_expiry');

        if (!token || !tokenExpiry) {
          console.error('Failed to store tokens after login');
          localStorage.removeItem('token');
          localStorage.removeItem('token_expiry');
          throw new Error('Failed to store authentication tokens');
        }

        console.log('Login successful, tokens stored');
        return { data: response.data, success: true };
      }

      console.error('Login response missing access token');
      return { error: 'Invalid server response', success: false };
    } catch (error) {
      const errorMessage = this.handleError(error);
      console.error('Login error:', errorMessage);
      // Ensure no partial auth state remains
      localStorage.removeItem('token');
      localStorage.removeItem('token_expiry');
      return { error: errorMessage, success: false };
    }
  }

  async refreshToken(): Promise<ApiResponse<AuthResponse>> {
    try {
      console.log('Attempting token refresh');
      const response = await this.client.post('/auth/refresh', {}, {
        withCredentials: true,
      });

      if (response.data.access_token) {
        // Set tokens in localStorage
        localStorage.setItem('token', response.data.access_token);
        localStorage.setItem('token_expiry', String(Date.now() + (response.data.expires_in * 1000)));

        // Verify token storage
        const token = localStorage.getItem('token');
        const tokenExpiry = localStorage.getItem('token_expiry');

        if (!token || !tokenExpiry) {
          console.error('Failed to store tokens after refresh');
          localStorage.removeItem('token');
          localStorage.removeItem('token_expiry');
          throw new Error('Failed to store authentication tokens');
        }

        console.log('Token refresh successful');
        return { data: response.data, success: true };
      }

      console.error('Refresh response missing access token');
      return { error: 'Invalid server response', success: false };
    } catch (error) {
      const errorMessage = this.handleError(error);
      console.error('Token refresh error:', errorMessage);
      // Ensure no partial auth state remains
      localStorage.removeItem('token');
      localStorage.removeItem('token_expiry');
      return { error: errorMessage, success: false };
    }
  }

  async logout(): Promise<ApiResponse<void>> {
    try {
      console.log('Attempting logout');
      await this.client.post('/auth/logout', {}, {
        withCredentials: true,
      });

      // Clear all authentication state
      localStorage.removeItem('token');
      localStorage.removeItem('token_expiry');

      // Verify cleanup
      const token = localStorage.getItem('token');
      const tokenExpiry = localStorage.getItem('token_expiry');

      if (token || tokenExpiry) {
        console.error('Failed to clear authentication state');
        // Force clear again
        localStorage.removeItem('token');
        localStorage.removeItem('token_expiry');
      }

      console.log('Logout successful');
      return { success: true };
    } catch (error) {
      const errorMessage = this.handleError(error);
      console.error('Logout error:', errorMessage);
      // Ensure auth state is cleared even on error
      localStorage.removeItem('token');
      localStorage.removeItem('token_expiry');
      return { error: errorMessage, success: false };
    }
  }

