import { useState, useCallback, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import apiClient from '../api/client';

export interface User {
  id: string;
  email: string;
  username: string;
  roles: Array<{
    name: string;
    permissions: string[];
  }>;
}

export interface AuthContextType {
  user: User | null;
  isAuthenticated: boolean;
  isLoading: boolean;
  signup: (email: string, username: string, password: string) => Promise<boolean>;
  login: (formData: { email: string; password: string }) => Promise<boolean>;
  logout: () => Promise<void>;
  refreshToken: () => Promise<boolean>;
}

// Token management helpers
const setTokens = (access_token: string, expires_in: number) => {
  localStorage.setItem('token', access_token);
  localStorage.setItem('token_expiry', String(Date.now() + (expires_in * 1000)));
};

const clearTokens = () => {
  localStorage.removeItem('token');
  localStorage.removeItem('token_expiry');
};

export const useAuthContext = (): AuthContextType => {
  const navigate = useNavigate();
  const [user, setUser] = useState<User | null>(null);
  const [isAuthenticated, setIsAuthenticated] = useState<boolean>(() => {
    const token = localStorage.getItem('token');
    return !!token;
  });
  const [isLoading, setIsLoading] = useState<boolean>(false);

  const signup = useCallback(async (email: string, username: string, password: string) => {
    setIsLoading(true);
    try {
      const response = await apiClient.signup({ email, username, password });
      if (response.success && response.data?.access_token) {
        setTokens(response.data.access_token, response.data.expires_in);
        // Set user data
        const newUser: User = {
          id: username,
          email,
          username,
          roles: [{ name: 'backend_developer', permissions: ['execute_market_maker_trades'] }]
        };
        setUser(newUser);
        setIsAuthenticated(true);
        // Ensure localStorage is updated before navigation
        await new Promise(resolve => setTimeout(resolve, 100));
        navigate('/agent-selection');
        return true;
      }
      throw new Error(response.error || 'Signup failed');
    } catch (error) {
      console.error('Signup error:', error);
      throw error instanceof Error ? error : new Error('An unexpected error occurred');
    } finally {
      setIsLoading(false);
    }
  }, [navigate]);

  const login = useCallback(async (formData: { email: string; password: string }) => {
    setIsLoading(true);
    try {
      const response = await apiClient.login({ username: formData.email, password: formData.password });
      if (response.success && response.data?.access_token) {
        setTokens(response.data.access_token, response.data.expires_in);

        // Set user data using email as identifier
        const newUser: User = {
          id: formData.email,
          email: formData.email,
          username: formData.email.split('@')[0],
          roles: [{ name: 'backend_developer', permissions: ['execute_market_maker_trades'] }]
        };
        setUser(newUser);
        setIsAuthenticated(true);
        navigate('/agent-selection');
        return true;
      }
      throw new Error(response.error || 'Login failed');
    } catch (error) {
      console.error('Login error:', error);
      throw error instanceof Error ? error : new Error('An unexpected error occurred');
    } finally {
      setIsLoading(false);
    }
  }, [navigate]);

  // Function to refresh the access token
  const refreshToken = useCallback(async () => {
    try {
      const response = await apiClient.refreshToken();
      if (response.success && response.data?.access_token) {
        localStorage.setItem('token', response.data.access_token);
        localStorage.setItem('token_expiry', String(Date.now() + (response.data.expires_in * 1000)));
        return true;
      }
      return false;
    } catch (error) {
      console.error('Token refresh failed:', error);
      return false;
    }
  }, []);

  const logout = useCallback(async () => {
    try {
      await apiClient.logout();
    } catch (error) {
      console.error('Logout error:', error);
    } finally {
      clearTokens();
      setUser(null);
      setIsAuthenticated(false);
      navigate('/login');
    }
  }, [navigate]);

  // Add token expiry check effect
  useEffect(() => {
    const checkTokenExpiry = async () => {
      const tokenExpiry = localStorage.getItem('token_expiry');
      if (!tokenExpiry) {
        await logout();
        return;
      }

      const expiryTime = parseInt(tokenExpiry);
      // Refresh token if it expires in less than 5 minutes
      if (Date.now() + 5 * 60 * 1000 >= expiryTime) {
        const success = await refreshToken();
        if (!success) {
          await logout();
        }
      }
    };

    // Initial check
    checkTokenExpiry();

    // Check every 4 minutes
    const interval = setInterval(checkTokenExpiry, 4 * 60 * 1000);
    return () => clearInterval(interval);
  }, [refreshToken, logout]);

  return {
    user,
    isAuthenticated,
    isLoading,
    signup,
    login,
    logout,
    refreshToken,
  };
};
